import groovy.util.XmlSlurper
import com.eviware.soapui.support.xml.XmlUtils

log.info " Attachment Name: " + testRunner.testCase.getTestStepByName( "receiveDocuments").testStep.testRequest.response.attachments[0].getName();
log.info " Attachment ContentType: " + testRunner.testCase.getTestStepByName( "receiveDocuments").testStep.testRequest.response.attachments[0].getContentType();

def attachment_stream = testRunner.testCase.getTestStepByName( "receiveDocuments").testStep.testRequest.response.attachments[0].getInputStream();
InputStream base64DecoderStream = null;
base64DecoderStream = javax.mail.internet.MimeUtility.decode(attachment_stream, "base64");
java.util.zip.GZIPInputStream gis = new java.util.zip.GZIPInputStream(base64DecoderStream);

def text = gis.text;
//log.info "xml " + text;
def decIds = text.findAll(/DecId>(\d+)</)
log.info "decIds " + decIds;


def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )  
holder = groovyUtils.getXmlHolder("markDocumentsReceived#Request")
holder.removeDomNodes(["//dhl:markDocumentsReceived[1]/keha[1]/dokumendid[1]/*"])
holder["//dhl:markDocumentsReceived[1]/keha[1]/dokumendid[1]"] = '''

'''
holder.updateProperty()
context.requestContent = holder.xml

documentNode = holder.getDomNode("//dhl:markDocumentsReceived[1]/keha[1]/dokumendid[1]")

decIds.each {
	def decId = it.substring(it.indexOf(">") + 1, it.indexOf("<"));
	log.info "DecId: " + decId;
	XmlUtils.addChildElement(documentNode, "item", "")

	def item = holder.getDomNode("//dhl:markDocumentsReceived[1]/keha[1]/dokumendid[1]/item[last()]")
	XmlUtils.addChildElement(item, "dhl_id", decId)
}

holder.updateProperty()
context.requestContent = holder.xml

log.info "Request: " +  holder.xml;
